import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class LoginService {

  constructor(
    private http: HttpClient
  ) { }

  loginUser(obj: any) {
    this.http.post("https://api.freeprojectapi.com/api/SmartParking/login", obj)
  }
}
